CREATE OR REPLACE VIEW AI_FEATURE_HUB.V_BILLING_MONTH AS SELECT ACCOUNT_ID,DATE_TRUNC('MONTH',USAGE_TIMESTAMP) AS BILLING_DATE,FEATURE_KEY,SUM(UNITS) AS TOTAL_UNITS,AVG(UNIT_PRICE) AS AVG_UNIT_PRICE,SUM(UNITS*UNIT_PRICE) AS TOTAL_COST FROM AI_FEATURE_HUB.TENANT_FEATURE_USAGE GROUP BY ACCOUNT_ID,BILLING_DATE,FEATURE_KEY;