CREATE OR REPLACE TABLE AI_FEATURE_HUB.INVOICE_PAYMENTS(PAYMENT_ID STRING PRIMARY KEY,INVOICE_ID STRING,AMOUNT NUMBER,CURRENCY STRING,STATUS STRING,TRANSACTION_ID STRING,PAYMENT_METHOD STRING,PAID_AT TIMESTAMP_LTZ,CREATED_AT TIMESTAMP_LTZ DEFAULT CURRENT_TIMESTAMP());CREATE OR REPLACE TABLE AI_FEATURE_HUB.BILLING_ADJUSTMENTS(ADJUSTMENT_ID STRING PRIMARY KEY,INVOICE_ID STRING,AMOUNT NUMBER(38,2),REASON STRING,CREATED_AT TIMESTAMP_LTZ DEFAULT CURRENT_TIMESTAMP());GRANT SELECT,INSERT,UPDATE ON ALL TABLES IN SCHEMA AI_FEATURE_HUB TO ROLE YOUR_RUN_ROLE;