CREATE OR REPLACE PROCEDURE AI_FEATURE_HUB.RUN_BILLING(billing_date DATE,account_id STRING) RETURNS VARIANT LANGUAGE SQL AS $$ DECLARE total_cost FLOAT; BEGIN LET cur CURSOR FOR SELECT SUM(TOTAL_COST) FROM AI_FEATURE_HUB.V_BILLING_MONTH WHERE ACCOUNT_ID=:account_id AND BILLING_DATE=:billing_date; OPEN cur; FETCH cur INTO total_cost; CLOSE cur; RETURN total_cost; END; $$;