CREATE OR REPLACE PROCEDURE AI_FEATURE_HUB.CREATE_BILLING_PERIOD(account_id STRING,billing_date DATE) RETURNS VARIANT LANGUAGE SQL AS $$ INSERT INTO AI_FEATURE_HUB.BILLING_PERIODS(ACCOUNT_ID,START_DATE,END_DATE,STATUS) VALUES(:account_id,DATE_TRUNC('MONTH',:billing_date),LAST_DAY(:billing_date,'MONTH'),'open'); RETURN 'ok'; $$;