CREATE OR REPLACE PROCEDURE AI_FEATURE_HUB.POST_BILLING_EVENT(account_id STRING,event_type STRING,amount NUMBER,currency STRING,meta VARIANT) RETURNS VARIANT LANGUAGE SQL AS $$ INSERT INTO AI_FEATURE_HUB.BILLING_EVENTS(EVENT_ID,EVENT_TIMESTAMP,ACCOUNT_ID,EVENT_TYPE,AMOUNT,CURRENCY,META) VALUES(UUID_STRING(),CURRENT_TIMESTAMP(),:account_id,:event_type,:amount,:currency,:meta); $$;