CREATE OR REPLACE TASK AI_FEATURE_HUB.TASK_CHARGE_LATE_FEES WAREHOUSE='COMPUTE_WH' SCHEDULE='USING CRON 0 4 * * * UTC' AS BEGIN SELECT SYSTEM$SEND_EMAIL('my_notification_integration', 'dave.smith@example.com,dianne.smith@example.com', 'Email Alert: Late Fee Accounts', (SELECT OBJECT_AGG('account_id', OBJECT_CONSTRUCT(*)) FROM (SELECT a.ACCOUNT_ID, a.BALANCE, a.CURRENCY FROM AI_FEATURE_HUB.V_ACCOUNT_BALANCE a JOIN AI_FEATURE_HUB.BILLING_PERIODS b ON a.ACCOUNT_ID=b.ACCOUNT_ID WHERE b.STATUS='closed' AND a.BALANCE>0))); END;