CREATE OR REPLACE VIEW AI_FEATURE_HUB.V_BILLABLE_USAGE AS SELECT T.ACCOUNT_ID, T.FEATURE_KEY, T.UNITS, T.UNIT_PRICE, T.UNITS * T.UNIT_PRICE AS TOTAL_PRICE, T.USAGE_TIMESTAMP, M.DEFAULT_MARKUP_PCT, T.UNITS * T.UNIT_PRICE * (1 + M.DEFAULT_MARKUP_PCT) AS MARKED_UP_PRICE FROM AI_FEATURE_HUB.TENANT_FEATURE_USAGE T JOIN AI_FEATURE_HUB.ACCOUNT_MARKUP M ON T.ACCOUNT_ID = M.ACCOUNT_ID;