CREATE OR REPLACE VIEW AI_FEATURE_HUB.V_INVOICE_DETAILS AS SELECT i.INVOICE_ID, i.ACCOUNT_ID, i.SUBTOTAL, i.MARKUP, i.TAX, i.TOTAL, i.CURRENCY, i.CREATED_AT, p.PAYMENT_ID, p.AMOUNT AS PAID_AMOUNT, p.STATUS AS PAYMENT_STATUS, p.PAID_AT FROM AI_FEATURE_HUB.SUBSCRIPTION_INVOICES i LEFT JOIN AI_FEATURE_HUB.INVOICE_PAYMENTS p ON i.INVOICE_ID = p.INVOICE_ID;